mapper.select:
SELECT
FROM tb_found AS tl INNER JOIN tb_users AS tu ON tl.user_id = tu.id INNER JOIN ( SELECT id,category_name FROM tb_category
category_name = #{categoryName,jdbcType=VARCHAR}
) AS tc ON tl.category_id = tc.id
tl.found_place LIKE "%"#{foundPlace,jdbcType=VARCHAR}"%"
AND tl.found_time BETWEEN #{foundTime1,jdbcType=TIMESTAMP} AND #{foundTime2,jdbcType=TIMESTAMP}
